'use strict';

var game;

module.exports = {	
	run: function(snake, fruit, field) {
		if (snake.move(fruit, field)) {
			this.end();
		}
	},

	start: function(snake, fruit, field){
		game = setInterval(this.run.bind(this, snake, fruit, field), snake.speed);
	},

	end: function(){
		clearInterval(game);
	}
};